class CJuego
{
private:
	vector<CPartida*> rondaPartidas;
	Tiempo* cronometro;
	Personaje* objPersonaje;
	Aliado* objAliado;
	int numeroRonda;
	bool aliadoCogido;
	bool yaNoMuestresAliado;
public:
	CJuego()
	{
		rondaPartidas.push_back(new CPartida());
		objPersonaje = new Personaje();
		cronometro = new Tiempo();
		objAliado = new Aliado();
		numeroRonda = 0;
		aliadoCogido = false;
	}

	~CJuego() {}

	bool victoria() {
		return objPersonaje->getPuntosAcumulados() == 50;
	}
	bool derrota(int& vidas) {
		return vidas == 0;
	}

	void teclado(char letra) {
		if (letra == 'W') objPersonaje->mover(letra);
		if (letra == 'A') objPersonaje->mover(letra);
		if (letra == 'S') objPersonaje->mover(letra);
		if (letra == 'D') objPersonaje->mover(letra);
	}

	void animaPersonaje()
	{
		objPersonaje->borrar();

		if (kbhit()) {
			teclado(toupper(_getch()));
		}
		objPersonaje->dibujar();

	};

	void barraLateral() {
		mostrarVidas(vidas);
		setxy(100, 15);
		objPersonaje->mostrarPuntos();
	}

	void partida(bool& irAlMenu)
	{
		playmusic();
		irAlMenu = false;
		color(992);
		rondaPartidas[0]->mostrarEnunciados();
		barraLateral();
		while (!irAlMenu)
		{
			if (numeroRonda >= 3 && !aliadoCogido) {
				objAliado->borrar();
				int eje;
				eje = objAliado->validarMov(80, 35, 15, 16);
				if (eje) {
					objAliado->movInvalido(eje);
				}
				objAliado->mover();
				objAliado->dibujar();
			}
			if (aliadoCogido) {
				objAliado->borrar();
				objAliado->setX(1);
				objAliado->setY(3);
				objAliado->setDx(0);
			}

			cronometro->mostrarTiempo();
			animaPersonaje();

			rondaPartidas[0]->animacion();


			if (rondaPartidas[0]->verificarColisiones(objPersonaje, objAliado, aliadoCogido))
			{
				if (victoria()) {
					printYouWin();
					system("pause>0");
					break;
				}
				if (derrota(vidas)) {
					printPerdiste();
					system("pause>0");
					break;
				}
				else {
					resetearPartida();
				}
			}
			_sleep(60);

		}
		irAlMenu = true;
		if (irAlMenu) menuFinal();
	};

	void resetearPartida()
	{
		numeroRonda++;
		rondaPartidas[0]->getControladorPelotas()->at(0)->borrar();
		rondaPartidas[0]->getControladorPelotas()->at(1)->borrar();
		rondaPartidas[0]->getObjPregunta()->borrarPregunta();
		rondaPartidas[0]->getObjPregunta()->borrarAlternativas();

		rondaPartidas.erase(rondaPartidas.begin() + 0);
		rondaPartidas.push_back(new CPartida());
		partida(irAlMenu);
	}
};