void printJuegoMatriz() {
	for (size_t i = 0; i < 40; i++)
	{
		for (size_t j = 0; j < 120; j++)
		{
			Console::SetCursorPosition(j, i);

			if (Juego[i][j] == 0) color(11); cout << char(219);
			if (Juego[i][j] == 1) color(14); cout << char(219);
			if (Juego[i][j] == 2) color(4); cout << char(219);
			if (Juego[i][j] == 3) color(10); cout << char(219);
			if (Juego[i][j] == 4) color(12); cout << char(219);
			if (Juego[i][j] == 7) color(0); cout << char(219);

		}
	}
}

char primerPregunta[] = "Si Maria habla mas bajo que Carmen y Lola habla mas alto que Carmen,  Maria habla mas alto o mas bajo que Lola?";

void escribirOracion(const char oracion[]) {
	int length = strlen(oracion);
	int x = 31;
	int y = 7;
	color(110);
	for (int i = 0; i < length; i++) {
		setxy(x, y);
		cout << oracion[i];

		if (x == 65) {
			cout << "\n";
			y++;
			x = 30;
		}
		x++;
		_sleep(50);

	}
}

void partida() {
	printJuegoMatriz();
	escribirOracion(primerPregunta);
	system("pause>0");
}





